/n #include <stdio.h>
 void calculator(int x, int y)
 {
 	int ans;
 	char opt;
 	printf("Enter operation from these [+,-,*,/]");
 	scanf(" %c", &opt);
 	switch (opt)
 	{
 	case '+':
 	ans = x + y;
 	printf("a + b = %d", ans);
 	break;
 	case '-':
 	ans = x - y;
 	printf("a - b = %d", ans);
 	break;
 	case '*':
 	ans = x * y;
 	printf("a * b = %d", ans);
 	break;
 	case '/':
 	ans = x / y;
 	printf("a / b = %d", ans);
 	break;
 	default:
 	printf("INVALID OPTION");
 	}
 }

 void main()
 {
 	int a, b, opt;
 	printf("a = ");
 	scanf("%d", &a);
 	printf("b = ");
 	scanf("%d", &b);
 	calculator(a, b);
 }

