#include<stdio.h>
 void swap(int a,int b){
 	int temp;
 	a=temp;
 	a=b;
 	b=temp;
 	printf("AFTER SWAPPING\n");
 	printf("x=%d\n"y=%d",a,b); 
}
 void main(){
 	int x,y;
 	printf("x = ");
 	scanf("%d", &x);
 	printf("y = ");
 	scanf("%d", &y);
 	swap(a,b);
 }

