#include<stdio.h>
 void swap(int *a,int *b){
 	&a=&a+&b;
 	&b=&a-&b;
 	&a=&a-&b;
 }
 void main(){
 	int x,y;
 	printf("x = ");
 	scanf("%d", &x);
 	printf("y = ");
 	scanf("%d", &y);
 	printf("AFTER SWAPPING\n");
 	swap(&x,&y);
 	printf("x=%d\n"y=%d",x,y);
 }

#include<stdio.h>
 int* sum(int *a,int *b){
 	int sum;
 	sum = *a+*b;
 	return &sum;
 }
 void main(){
 	int x,y;
 	int *total;
 	printf("x = ");
 	scanf("%d", &x);
 	printf("y = ");
 	scanf("%d", &y);
 	total = sum(&x,&y);
 	printf("x + y =%d",*total);
 }

