#include<stdio.h>
void main(){
	int a[3][3],i,j;
	printf("Enter values \n");
	for(i=0;i<3;i++){
	for(j=0;j<3;j++){
		scanf("%d",&a[i][j]);
	}}
	printf("Elements of left diagonal are -:\t");
	for(i=0;i<3;i++){
	for(j=0;j<3;j++){
	if(i==j){
	printf("%d\t",a[i][j]);
	}}
	printf("\nElements of right diagonal are -:\t");
	for(i=0;i<3;i++){
	for(j=0;j<3;j++){
	if(i+j==2){
	printf("%d\t",a[i][j]);
	}}
	}

