#include<stdio.h>
 struct info
 {
	 int r_no;
	 char email[30];
	 char name[20];
 };

 int main()
 {
 struct info inf;
 printf("Enter Details : ");
 printf("\nRoll no\t");
 scanf("%d", &inf.r_no);
 printf("em@il id\t");
 scanf("%s", inf.email);
 printf("name\t");
 scanf("%s", inf.name);
 printf("\n\nDetails entered are");
 printf("\nRoll no\t%d\n", inf.r_no);
 printf("em@il id\t%s\n", inf.email);
 printf("name\t%s", inf.name);
 }

