 #include<stdio.h>
 #include<stdlib.h>
 struct demo
 {
 int *a, *b;
 int data;
 };
 void main()
 {
 struct demo *d;
 int a, b;
 d = malloc(sizeof(struct demo));
 printf("\nEnter a:\t");
 scanf("\n%d", &a);
 printf("\nb:\t");
 scanf("\n%d", &b);
 printf("\ndata:\t");
 scanf("\n%d", &d->data);
 d-<a = &a;
 d-<b = &b;
 printf("\n\n\nDetails entered are :\n");
 printf("\na:\t %d \n b:\t %d \n data:\t%d",*d->a,*d->b,d->data);
 }

