 	{
 	printf("Entername%d\t",i);
 	scanf("%[^\n]%*c",s);
 	fprintf(f,"Name:%s\n",s);
 	}
 	fclose(f);

	f=fopen("names.txt","r");
 	do
 	{
 	ch=fgetc(f);
 	printf("%c",ch);
 	}while(ch!=EOF);
 	fclose(f);
 	}

#include<stdio.h>
 #include<stdlib.h>
 #include<string.h>
 struct student
 {
 int rollno;
 char name[30];
 };

 void main()
 {
 FILE *f;
 f = fopen("std.txt", "w");
 struct student s, rd;
 if (f == NULL)
 {
 fprintf(stderr, "\nERROR unable to open file");
 exit(1);
 }
 printf("Enter Name\t");
 gets(s.name);
 printf("Enter Roll No\t");
 scanf("%d", &s.rollno);
 fwrite(&s, sizeof(struct student), 1, f);
 fclose(f);
 f = fopen("std.txt", "r");
 if (f == NULL)
 {
 fprintf(stderr, "\nEmpty File");
 exit(1);
 }
 fread(&rd, sizeof(struct student), 1, f);
 printf("\nName\t%s\nRoll No.\t%d", rd.name, rd.rollno);
 }

#include<stdio.h>
 enum days
 {
 sun,
 mon,
 tue,
 wed,
 thurs,
 fri,
 sat
 };
 int main()
 {
 enum days day;
 day = thurs;
 while (day != 0)
 {
 printf("No Holiday\n");
 day=day-1;
 if (day == 0)
 printf("Sunday");
 }
 return 0;
 }


