#include<stdio.h>
void main()
{
	int n, rev = 0;
	printf(<Enter Number >);
	scanf("%d", &n);
	while (n != 0)
	{
	rev = rev * 10 + (n % 10);
	n = n / 10;
	}
	printf(<Reverse Value is %d>,rev);
}

#include<stdio.h>
void main(){
	int i;
	scanf("%d",&i);
	switch(i){
		Case 1:
		printf("ONE");
		break;
		Case 2:
		printf("TWO");
		break;
		Case 3:
		printf("THREE");
		break;
		Case 4:
		printf("FOUR");
		break;
		Case 5:
		printf("FIVE");
		break;
		default:
		printf("INVALID OPTION"); 	}
}

/n #include <stdio.h>
 void calculator(int x, int y)
 {
 	int ans;
 	char opt;
 	printf("Enter operation from these [+,-,*,/]");
 	scanf("%c", &opt);
 	switch (opt)
 	{
 	case '+':
 	ans = x + y;
 	printf("a + b = %d", ans);
 	break;
 	case '-':
 	ans = x - y;
 	printf("a - b = %d", ans);
 	break;
 	case '*':
 	ans = x * y;
 	printf("a * b = %d", ans);
 	break;
 	case '/':
 	ans = x / y;
 	printf("a / b = %d", ans);
 	break;
 	default:
 	printf("INVALID OPTION");
 	}
 }

 void main()
 {
 	int a, b, opt;
 	printf("a = ");
 	scanf("%d", &a);
 	printf("b = ");
 	scanf("%d", &b);
 	calculator(a, b);
 }

